window.onload=()=>{setupNavbarToggle();updateNavbarStyleOnScroll();};function updateNavbarStyleOnScroll(){updateNavbarStyle();window.addEventListener("scroll",throttle(updateNavbarStyle),{passive:true,});}
function updateNavbarStyle(){const distanceY=window.pageYOffset||$(document).scrollTop();document.body.classList.toggle("scrolled",distanceY>55);}
const throttle=(func,delay=300)=>{let waiting;return function(...args){if(waiting)return;waiting=true;setTimeout(()=>{func(args);waiting=false;},delay);};};function scrollToTop(){document.body.scrollTop=0;document.documentElement.scrollTop=0;}
function setupNavbarToggle(){document.querySelectorAll(".nav-item").forEach((l)=>{l.addEventListener("click",()=>{$("#navbarSupportedContent").collapse("hide");});});}
const breakpoint={sm:576,md:768,xl:1200};$('.carousel .carousel-item').each(function(){var minPerSlide=4;var next=$(this).next();if(!next.length){next=$(this).siblings(':first');}
next.children(':first-child').clone().appendTo($(this));for(var i=0;i<minPerSlide;i++){next=next.next();if(!next.length){next=$(this).siblings(':first');}
next.children(':first-child').clone().appendTo($(this));}});